import React, { useState } from "react";
import Uppy from "@uppy/core";
import { Dashboard } from "@uppy/react";
import "@uppy/core/dist/style.min.css";
import "@uppy/dashboard/dist/style.min.css";

import VoiceRecorder from "./VoiceRecorder"; // <- your custom recorder

const MemoryInput = ({ onSubmit, isLoading }) => {
  const [textMemory, setTextMemory] = useState("");
  const [recordedAudioBlob, setRecordedAudioBlob] = useState(null);
  const [uploadedFile, setUploadedFile] = useState(null);

  // Set up Uppy instance
  const uppy = new Uppy({
    restrictions: {
      maxNumberOfFiles: 1,
      allowedFileTypes: [".mp3", ".wav", ".m4a", "audio/*"],
    },
    autoProceed: true,
  });

  uppy.on("file-added", (file) => {
    setUploadedFile(file.data);
  });

  uppy.on("file-removed", () => {
    setUploadedFile(null);
  });

  const handleSubmit = (e) => {
    e.preventDefault();

    const memoryData = {
      text: textMemory,
      audioBlob: recordedAudioBlob || uploadedFile || null,
    };

    onSubmit(memoryData);
  };

  return (
    <div className="max-w-2xl mx-auto bg-white p-8 rounded-xl shadow-md">
      <form onSubmit={handleSubmit}>
        <div className="mb-6">
          <label
            htmlFor="memory-text"
            className="block text-sm font-medium text-gray-700 mb-2"
          >
            Describe your memory
          </label>
          <textarea
            id="memory-text"
            placeholder="Tell us about a moment you'd like to transform..."
            className="w-full p-3 border border-gray-300 rounded-md focus:ring-2 focus:ring-primary focus:border-primary"
            rows="4"
            value={textMemory}
            onChange={(e) => setTextMemory(e.target.value)}
            required
          />
        </div>

        {/* Voice recording */}
        <VoiceRecorder onAudioCapture={(blob) => setRecordedAudioBlob(blob)} />

        {/* Audio file upload */}
        <div className="mb-6">
          <p className="block text-sm font-medium text-gray-700 mb-2">
            Or upload your voice file
          </p>
          <Dashboard
            uppy={uppy}
            proudlyDisplayPoweredByUppy={false}
            height={200}
            showProgressDetails
          />
        </div>

        <div className="text-center">
          <button
            type="submit"
            className="px-6 py-3 bg-primary text-white rounded-lg hover:bg-opacity-90 transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
            disabled={
              isLoading || (!textMemory && !recordedAudioBlob && !uploadedFile)
            }
          >
            {isLoading ? (
              <span className="flex items-center justify-center">
                <svg
                  className="animate-spin -ml-1 mr-3 h-5 w-5 text-white"
                  xmlns="http://www.w3.org/2000/svg"
                  fill="none"
                  viewBox="0 0 24 24"
                >
                  <circle
                    className="opacity-25"
                    cx="12"
                    cy="12"
                    r="10"
                    stroke="currentColor"
                    strokeWidth="4"
                  ></circle>
                  <path
                    className="opacity-75"
                    fill="currentColor"
                    d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
                  ></path>
                </svg>
                Processing...
              </span>
            ) : (
              "Transform Memory"
            )}
          </button>
        </div>
      </form>
    </div>
  );
};

export default MemoryInput;
