import React, { useState } from "react";
import MemoryInput from "./components/MemoryInput";
import MemoryDisplay from "./components/MemoryDisplay";
import "./index.css";

function App() {
  const [memory, setMemory] = useState(null);
  const [loading, setLoading] = useState(false);

  // Function to handle the memory submission
  const handleMemorySubmit = async (memoryData) => {
    setLoading(true);
    try {
      // In a real app, this would be calling your backend API
      // For now, we'll mock a response with a 2 second delay
      await new Promise((resolve) => setTimeout(resolve, 2000));

      // Mock response from the backend
      const mockedResponse = {
        story:
          "It was a warm summer evening when you decided to take that spontaneous road trip to the coast. The windows were down, music playing, and the salty breeze welcomed you as you approached the shoreline. You remember laughing with your friends, taking pictures of the sunset, and feeling completely free. That night under the stars, sharing stories around a small bonfire, was when you realized how precious these moments are - the ones that seem so ordinary yet become the memories we treasure most.",
        imageUrl:
          "https://placehold.co/600x400/e2e8f0/475569?text=Generated+Memory+Image",
        audioUrl: "https://www2.cs.uic.edu/~i101/SoundFiles/StarWars3.wav", // Sample audio for testing
      };

      setMemory(mockedResponse);
    } catch (error) {
      console.error("Error processing memory:", error);
      alert("There was an error processing your memory. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-background">
      <div className="memoir-container py-10">
        <header className="text-center mb-12">
          <h1 className="text-4xl font-bold text-gray-800 mb-2">Memoir AI</h1>
          <p className="text-lg text-gray-600">
            Transform your memories into stories
          </p>
        </header>

        {!memory ? (
          <MemoryInput onSubmit={handleMemorySubmit} isLoading={loading} />
        ) : (
          <div>
            <MemoryDisplay memory={memory} />
            <div className="mt-8 text-center">
              <button
                onClick={() => setMemory(null)}
                className="px-6 py-2 bg-primary text-white rounded-lg hover:bg-opacity-90 transition-colors"
              >
                Create New Memory
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
