import React from "react";

const MemoriesGallery = () => {
  // Sample memory data - these would come from your backend in a real app
  const memories = [
    {
      id: 1,
      imageUrl: "https://images.unsplash.com/photo-1516493499486-75c9d8577cec",
      type: "image",
      title: "Ancient Tree",
    },
    {
      id: 2,
      imageUrl: "https://images.unsplash.com/photo-1579773490308-cbf0cc5cbf61",
      type: "image",
      title: "Canyon River",
    },
    {
      id: 3,
      imageUrl: "https://images.unsplash.com/photo-1518837695005-2083093ee35b",
      type: "video",
      title: "Ocean Wave",
    },
    {
      id: 4,
      imageUrl: "https://images.unsplash.com/photo-1513836279014-a89f7a76ae86",
      type: "image",
      title: "Forest Sunset",
    },
    {
      id: 5,
      imageUrl: "https://images.unsplash.com/photo-1546489545-697049c0b528",
      type: "video",
      title: "Rolling Hills",
    },
    {
      id: 6,
      imageUrl: "https://images.unsplash.com/photo-1475070929565-c985b496cb9f",
      type: "video",
      title: "Mountain River",
    },
  ];

  return (
    <div className="w-full">
      <h2 className="text-4xl font-bold text-gray-800 mb-8">Your Memories</h2>
      <div className="grid grid-cols-1 sm:grid-cols-2 xl:grid-cols-4 gap-6">
        {memories.map((memory) => (
          <div
            key={memory.id}
            className="relative rounded-lg overflow-hidden shadow-md hover:shadow-xl transition-shadow duration-300"
            style={{ aspectRatio: "1/1" }}
          >
            <img
              src={memory.imageUrl}
              alt={memory.title}
              className="w-full h-full object-cover"
            />
            {memory.type === "video" && (
              <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2">
                <div className="bg-black bg-opacity-30 rounded-full p-2">
                  <svg
                    className="w-10 h-10 text-white"
                    fill="currentColor"
                    viewBox="0 0 20 20"
                    xmlns="http://www.w3.org/2000/svg"
                  >
                    <path
                      fillRule="evenodd"
                      d="M10 18a8 8 0 100-16 8 8 0 000 16zM9.555 7.168A1 1 0 008 8v4a1 1 0 001.555.832l3-2a1 1 0 000-1.664l-3-2z"
                      clipRule="evenodd"
                    />
                  </svg>
                </div>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default MemoriesGallery;
