import React, { useEffect, useRef } from "react";

const ElevenLabsConvai = () => {
  const widgetRef = useRef(null);
  const containerRef = useRef(null);

  useEffect(() => {
    const script = document.createElement("script");
    script.src = "https://elevenlabs.io/convai-widget/index.js";
    script.async = true;
    script.type = "text/javascript";

    // Add load event listener to ensure script is loaded before creating the widget
    script.onload = () => {
      // Create the widget element after script is loaded
      const widget = document.createElement("elevenlabs-convai");
      widget.setAttribute("agent-id", "RWbsVT7HJXMIoFe1EnU6");
      widget.style.position = "static";
      widget.style.width = "100%";
      containerRef.current.appendChild(widget);
    };

    // Add error handler
    script.onerror = (error) => {
      console.error("Error loading ElevenLabs widget script:", error);
    };

    // Append script to document head instead of the container
    document.head.appendChild(script);

    // Cleanup function
    return () => {
      document.head.removeChild(script);
      if (containerRef.current) {
        containerRef.current.innerHTML = "";
      }
    };
  }, []);

  return (
    <div
      ref={widgetRef}
      className="mt-6 mx-auto w-full max-w-xs h-full border border-gray-300 rounded-lg p-2 flex flex-col justify-end"
      style={{ minHeight: "calc(100vh - 200px)" }} // Adjust height to push it toward the bottom
    >
      <div ref={containerRef}></div>
    </div>
  );
};

export default ElevenLabsConvai;
