import React from "react";

const MemoriesGallery = () => {
  const memories = [
    {
      id: 1,
      imageUrl: "https://images.unsplash.com/photo-1516493499486-75c9d8577cec",
      type: "image",
      title: "Ancient Tree",
    },
    {
      id: 2,
      imageUrl: "https://images.unsplash.com/photo-1579773490308-cbf0cc5cbf61",
      type: "image",
      title: "Canyon River",
    },
    {
      id: 3,
      imageUrl: "https://images.unsplash.com/photo-1518837695005-2083093ee35b",
      type: "video",
      title: "Ocean Wave",
    },
    {
      id: 4,
      imageUrl: "https://images.unsplash.com/photo-1513836279014-a89f7a76ae86",
      type: "image",
      title: "Forest Sunset",
    },
    {
      id: 5,
      imageUrl: "https://images.unsplash.com/photo-1546489545-697049c0b528",
      type: "video",
      title: "Rolling Hills",
    },
    {
      id: 6,
      imageUrl: "https://images.unsplash.com/photo-1475070929565-c985b496cb9f",
      type: "video",
      title: "Mountain River",
    },
  ];

  return (
    <div className="w-full">
      <div className="grid grid-cols-1 sm:grid-cols-2 xl:grid-cols-4 gap-6">
        {memories.map((memory) => (
          <div
            key={memory.id}
            className="relative rounded-lg overflow-hidden shadow-md hover:shadow-xl transition-shadow duration-300 group"
            style={{ aspectRatio: "2/3" }}
          >
            <img
              src={memory.imageUrl}
              alt={memory.title}
              className="w-full h-full object-cover"
            />

            {/* Hover Overlay */}
            <div className="absolute inset-0 bg-black/50 opacity-0 group-hover:opacity-100 transition-opacity duration-300 flex items-center justify-center">
              <h3 className="text-white text-lg font-semibold text-center px-2">
                {memory.title}
              </h3>
            </div>

            {/* Play Icon for videos */}
            {memory.type === "video" && (
              <div className="absolute top-2 left-2 bg-black bg-opacity-60 rounded-full p-1">
                <svg
                  className="w-6 h-6 text-white"
                  fill="currentColor"
                  viewBox="0 0 20 20"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <path
                    fillRule="evenodd"
                    d="M10 18a8 8 0 100-16 8 8 0 000 16zM9.555 7.168A1 1 0 008 8v4a1 1 0 001.555.832l3-2a1 1 0 000-1.664l-3-2z"
                    clipRule="evenodd"
                  />
                </svg>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default MemoriesGallery;
