import React, { useState } from "react";
// import MemoryInput from "./components/MemoryInput";
import MemoryDisplay from "./components/MemoryDisplay";
import MediaUpload from "./components/MediaUpload";
import VoiceRecorder from "./components/VoiceRecorder";
import MemoriesGallery from "./components/MemoriesGallery";
import "./index.css";

function App() {
  const [memory, setMemory] = useState(null);
  const [loading, setLoading] = useState(false);
  const [showModal, setShowModal] = useState(false);
  const [mediaFile, setMediaFile] = useState(null);
  const [audioBlob, setAudioBlob] = useState(null);

  const handleMemorySubmit = async () => {
    setLoading(true);
    try {
      await new Promise((resolve) => setTimeout(resolve, 2000));

      const mockedResponse = {
        story:
          "It was a warm summer evening when you decided to take that spontaneous road trip to the coast. The windows were down, music playing, and the salty breeze welcomed you as you approached the shoreline. You remember laughing with your friends, taking pictures of the sunset, and feeling completely free. That night under the stars, sharing stories around a small bonfire, was when you realized how precious these moments are - the ones that seem so ordinary yet become the memories we treasure most.",
        imageUrl: mediaFile
          ? URL.createObjectURL(mediaFile)
          : "https://placehold.co/600x400/e2e8f0/475569?text=Generated+Memory+Image",
        audioUrl: audioBlob
          ? URL.createObjectURL(audioBlob)
          : "https://www2.cs.uic.edu/~i101/SoundFiles/StarWars3.wav",
      };

      setMemory(mockedResponse);
      setShowModal(false);
    } catch (error) {
      console.error("Error processing memory:", error);
      alert("There was an error processing your memory. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex min-h-screen">
      {/* Left Section - 75% */}
      <div className="w-1/4 p-6 bg-gray-100">
        {memory ? <MemoryDisplay memory={memory} /> : <MemoriesGallery />}
      </div>

      {/* Right Section - 25% */}
      <div className="w-3/4 p-4 flex flex-col justify-between bg-white shadow-xl">
        <div className="text-center">
          <h1 className="text-2xl font-bold text-gray-800">Memoir AI</h1>
        </div>
        <div className="text-center text-gray-400 mt-20">
          <p>No memory to display. Click "Create Memory" to begin.</p>
        </div>
        <div className="text-right">
          <button
            onClick={() => setShowModal(true)}
            className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700"
          >
            Create Memory
          </button>
        </div>
      </div>

      {/* Modal Popup */}
      {showModal && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50 z-50">
          <div className="w-3/5 bg-white p-8 rounded-xl shadow-xl relative">
            <h2 className="text-xl font-semibold text-center mb-4">
              Create a New Memory
            </h2>
            <MediaUpload onMediaSelect={(file) => setMediaFile(file)} />

            {mediaFile && (
              <div className="mt-4 mb-6 text-center">
                <h3 className="text-sm font-medium text-gray-700 mb-2">
                  Preview
                </h3>
                {mediaFile.type.startsWith("video") ? (
                  <video
                    src={URL.createObjectURL(mediaFile)}
                    controls
                    className="w-48 h-auto mx-auto rounded"
                  />
                ) : (
                  <img
                    src={URL.createObjectURL(mediaFile)}
                    alt="Uploaded"
                    className="w-48 h-auto mx-auto rounded"
                  />
                )}
              </div>
            )}

            <VoiceRecorder onAudioCapture={(blob) => setAudioBlob(blob)} />
            <div className="text-center mt-6">
              <button
                onClick={handleMemorySubmit}
                className="bg-green-600 text-white px-6 py-2 rounded hover:bg-green-700"
                disabled={loading}
              >
                {loading ? "Processing..." : "Transform Memory"}
              </button>
            </div>
            <button
              className="absolute top-4 right-4 text-gray-500 hover:text-gray-700"
              onClick={() => setShowModal(false)}
            >
              ✕
            </button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
