// components/ParticlesRightPanel.js
import React, { useRef, useEffect } from "react";

const ParticlesRightPanel = () => {
  const canvasRef = useRef(null);
  const dotsArray = useRef([]);
  const numberOfDots = 100;
  const colors = ["#6b7280"];

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    const parent = canvas.parentElement;
    const resizeCanvas = () => {
      canvas.width = parent.offsetWidth;
      canvas.height = parent.offsetHeight;
    };
    resizeCanvas();

    let speedFactor = 1.0;

    class Dot {
      constructor() {
        this.x = Math.random() * canvas.width;
        this.y = Math.random() * canvas.height;
        this.size = Math.random() * 3 + 1;
        this.speedX = (Math.random() * 2 - 1) * speedFactor;
        this.speedY = (Math.random() * 2 - 1) * speedFactor;
        this.maxBlur = Math.random() * 20 + 1;
        this.blur = this.maxBlur;
        this.blurDirection = -1;
        this.blurSpeed = Math.random() * 0.08 + 0.01;
        this.color = colors[Math.floor(Math.random() * colors.length)] + "30";
      }

      update() {
        this.x += this.speedX;
        this.y += this.speedY;
        if (this.x > canvas.width) this.x = 0;
        if (this.x < 0) this.x = canvas.width;
        if (this.y > canvas.height) this.y = 0;
        if (this.y < 0) this.y = canvas.height;
        this.blur += this.blurDirection * this.blurSpeed;
        if (this.blur <= 0) {
          this.blur = 0;
          this.blurDirection = 1;
        } else if (this.blur >= this.maxBlur) {
          this.blur = this.maxBlur;
          this.blurDirection = -1;
        }
      }

      draw() {
        ctx.save();
        ctx.fillStyle = this.color;
        ctx.beginPath();
        ctx.arc(this.x, this.y, this.size, 0, Math.PI * 2);
        ctx.closePath();
        ctx.fill();
        ctx.restore();
      }
    }

    function init() {
      dotsArray.current = [];
      for (let i = 0; i < numberOfDots; i++) {
        dotsArray.current.push(new Dot());
      }
    }

    function animate() {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      dotsArray.current.forEach((dot) => {
        dot.update();
        dot.draw();
      });
      requestAnimationFrame(animate);
    }

    init();
    animate();

    window.addEventListener("resize", resizeCanvas);
    return () => window.removeEventListener("resize", resizeCanvas);
  }, []);

  return (
    <canvas
      ref={canvasRef}
      style={{
        position: "absolute",
        inset: 0,
        width: "100%",
        height: "100%",
        zIndex: 0,
        pointerEvents: "none",
      }}
    />
  );
};

export default ParticlesRightPanel;
